4//indicator, Grove - LED is connected with D4 of Arduino\n    boolean led_state = LOW;//state of LED, each time an external interrupt \n                                    //will change the state of LED\n    unsigned char counter;\n    unsigned long temp[21];\n    unsigned long sub;\n    bool data_effect=true;\n    unsigned int heart_rate;//the measurement result of heart rate\n\n    const int max_heartpluse_duty = 2000;//you can change it follow your system\'s request.\n                            //2000 meams 2 seconds. System return error \n                            //if the duty overtrip 2 second.\n    void setup()\n    {\n        pinMode(LED, OUTPUT);\n        Serial.begin(9600);\n        Serial.println("Please ready your chest belt.");\n        delay(5000);\n        arrayInit();\n        Serial.println("Heart rate test begin.");\n        attachInterrupt(0, interrupt, RISING);//set interrupt 0,digital port 2\n    }\n    void loop()\n    {\n        digitalWrite(LED, led_state);//Update the state of the indicator\n    }\n    /*Function: calculate the heart rate*/\n    void sum()\n    {\n     if(data_effect)\n        {\n          heart_rate=1200000/(temp[20]-temp[0]);//60*20*1000/20_total_time \n          Serial.print("Heart_rate_is:\\t");\n          Serial.println(heart_rate);\n        }\n       data_effect=1;//sign bit\n    }\n    /*Function: Interrupt service routine.Get the sigal from the external interrupt*/\n    void interrupt()\n    {\n        temp[counter]=millis();\n        Serial.println(counter,DEC);\n        Serial.println(temp[counter]);\n        switch(counter)\n        {\n            case 0:\n                sub=temp[counter]-temp[20];\n                Serial.println(sub);\n                break;\n            default:\n                sub=temp[counter]-temp[counter-1];\n                Serial.println(sub);\n                break;\n        }\n        if(sub>max_heartpluse_duty)//set 2 seconds as max heart pluse duty\n        {\n            data_effect=0;//sign bit\n            counter=0;\n            Serial.println("Heart rate measure error,test will restart!" );\n            arrayInit();\n        }\n        if (counter==20&&data_effect)\n        {\n            counter=0;\n            sum();\n        }\n        else if(counter!=20&&data_effect)\n        counter++;\n        else \n        {\n            counter=0;\n            data_effect=1;\n        }\n        \n    }\n    /*Function: Initialization for the array(temp)*/\n    void arrayInit()\n    {\n        for(unsigned char i=0;i < 20;i ++)\n        {\n            temp[i]=0;\n        }\n        temp[20]=millis();\n    }\n')),(0,i.kt)("ul",null,(0,i.kt)("li",{parentName:"ul"},"Upload the code."),(0,i.kt)("li",{parentName:"ul"},"Make sure the sensor contacts your ear skin. This is the signal when we are measuring the heart rate:")),(0,i.kt)("div",{align:"center"},(0,i.kt)("img",{width:"{1000}",src:"https://files.seeedstudio.com/wiki/Grove-Ear-clip_Heart_Rate_Sensor/img/ardu_serial1.jpg"})),(0,i.kt)("div",{align:"center"},(0,i.kt)("img",{width:"{1000}",src:"https://files.seeedstudio.com/wiki/Grove-Ear-clip_Heart_Rate_Sensor/img/ardu_serial2.jpg"})),(0,i.kt)("p",null,"In the first of the figures, which is a waveform diagram of the detected heartbeat, a high pulse comes when beating."),(0,i.kt)("admonition",{type:"note"},(0,i.kt)("p",{parentName:"admonition"},"If the serial monitor return an error message, please change the position of the sensor.")),(0,i.kt)("h2",{id:"resources"},"Resources"),(0,i.kt)("ul",null,(0,i.kt)("li",{parentName:"ul"},(0,i.kt)("a",{parentName:"li",href:"https://files.seeedstudio.com/wiki/Grove-Ear-clip_Heart_Rate_Sensor/res/Grove-Heart_rate_chest_belt_V1.0.zip"},"Grove - Ear-clip Heart Rate Sensor Demo code")),(0,i.kt)("li",{parentName:"ul"},(0,i.kt)("a",{parentName:"li",href:"https://files.seeedstudio.com/wiki/Grove-Ear-clip_Heart_Rate_Sensor/res/PPG%20Sensor%20System.pdf"},"PPG Sensor System"))),(0,i.kt)("h2",{id:"projects"},"Project