//include the library\n#include "TFLidar.h"\n#define LINE_DIS 0X00\n#define STRING_DIS 0X01\nTFMini SeeedTFMini;\nTFLidar SeeedTFLidar(&SeeedTFMini);\n#define uart  Serial1\n\nTFT_eSPI tft;\n \n#define max_size 10 //maximum size of data\ndoubles data; //Initilising a doubles type to store data\nTFT_eSprite spr = TFT_eSprite(&tft);  // Sprite \n \nvoid setup() {\n    Serial.begin(115200);\n    pinMode(WIO_KEY_C, INPUT_PULLUP);\n    tft.begin();\n    tft.setRotation(3);\n    spr.createSprite(TFT_HEIGHT,TFT_WIDTH);\n    SeeedTFLidar.begin(&uart,115200);\n}\nuint8_t mode = LINE_DIS;\nvoid loop() {\n    \n    if (digitalRead(WIO_KEY_C) == LOW) {\n        mode ++;\n        if(mode > STRING_DIS ) mode = LINE_DIS;\n        while(!digitalRead(WIO_KEY_C));\n    }\n    display(get_Lidar_data(),mode);\n    delay(50);\n}\n\nuint16_t get_Lidar_data()\n{\n    while(!SeeedTFLidar.get_frame_data()){\n        delay(1); \n    }\n    return SeeedTFLidar.get_distance();\n}\n\nvoid display(uint16_t lidar_data,uint8_t mode){\n\n    spr.fillSprite(TFT_WHITE);\n    //Settings for the line graph title\n    auto header =  text(0, 0)\n                .value("Lidar Terminal")\n                .align(center)\n                .valign(vcenter)\n                .width(tft.width())\n                .color(green)\n                .thickness(3);\n    header.height(header.font_height() * 2);\n    header.draw(); //Header height is the twice the height of the font\n    if (LINE_DIS == mode){\n        if (data.size() == max_size) {\n            data.pop();//this is used to remove the first read variable\n        }\n        data.push(lidar_data); //read variables and store in data\n    //Settings for the line graph\n        auto content = line_chart(10, header.height()); //(x,y) where the line graph begins\n            content\n                    .height(tft.height() - header.height() * 1.5) //actual height of the line chart\n                    .width(tft.width() - content.x() * 2) //actual width of the line chart\n                    .based_on(0.0) //Starting point of y-axis, must be a float\n                    .show_circle(false) //drawing a cirle at each point, default is on.\n                    .value(data) //passing through the data to line graph\n                    .color(TFT_RED) //Setting the color for the line\n                    .draw();\n    }\n    else if (STRING_DIS == mode){\n        for(int8_t line_index = 0;line_index < 5 ; line_index++)\n        {\n            spr.drawLine(0, 50 + line_index, tft.width(), 50 + line_index, TFT_GREEN);\n        }        \n        auto header =  text(0, 0)\n                    .thickness(1);\n        spr.setFreeFont(&FreeSansBoldOblique24pt7b); \n        spr.setTextColor(TFT_BLUE);\n        spr.drawFloat(lidar_data / 100.00,2,80,110);\n        spr.drawString(" M",80 + 90,110,1);\n\n    }\n    spr.pushSprite(0, 0);\n\n}\n')),(0,i.kt)("admonition",{type:"success"},(0,i.kt)("pre",{parentName:"admonition"},(0,i.kt)("code",{parentName:"pre"}," An image will display on the screen of Wio terminal as below if everything goes well.\n"))),(0,i.kt)("p",null,(0,i.kt)("img",{parentName:"p",src:"https://files.seeedstudio.com/wiki/Grove-TF_Mini_LiDAR/img/Outcome_wio.jpg",alt:null})),(0,i.kt)("h2",{id:"resources"},"Resources"),(0,i.kt)("hr",null),(0,i.kt)("ul",null,(0,i.kt)("li",{parentName:"ul"},(0,i.kt)("strong",{parentName:"li"},"[Datasheet]")," ",(0,i.kt)("a",{parentName:"li",href:"https://files.seeedstudio.com/wiki/Grove-TF_Mini_LiDAR/res/SJ-PM-TF-Luna-A03-Product-Manual.pdf"},"Grove-TF-Mini-LiDAR\n")),(0,i.kt)("li",{parentName:"ul"},(0,i.kt)("strong",{parentName:"li"},"[Datasheet]")," ",(0,i.kt)("a",{parentName:"li",href:"https://files.seeedstudio.com/wiki/Grove-TF_Mini_LiDAR/res/DE-LiDAR%20TFmini%20Datasheet-V1.7-EN.pdf"},"Grove-TF-Luna-LiDAR\n")),(0,i.kt)("li",{parentName:"ul"},(0,i.kt)("strong",{parentName:"li"},"[ZIP]")," ",(0,i.kt)("a",{parentName:"li",href:"https://github.com/Seeed-Studio/Seeed_Arduino_TFlidar/archive